export class BDocCode extends BComponent {

  static propTypes = {
    content: PropTypes.string.isRequired,
    lang: PropTypes.string,
    highlight: PropTypes.boolean,
  };
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <pre>
        <code>{hljs.highlight(this.props.content)}</code>
      </pre>
    );
  }
}
export default BDocCode;
